export function getPlatformIcon(platform: string, size: IconSize = "md"): ReactNode {
  const className = sizeClassMap[size];

  switch (platform) {
    case "Android":
      return <AndroidRobotIcon className={className} />;
    case "iOS":
      return <AppleIcon className={className} />;
    case "Mac":
      return <MacIcon className={className} />;
    case "Windows":
      return <WindowsLogoIcon className={className} />;
    case "Linux":
      return <LinuxTuxIcon className={className} />;
    case "Web":
      return <GlobeIcon className={className} />;
    default:
      return <MonitorIcon className={className} />;
  }
}

export function getPlatformAccentClass(platform: string): string {
  switch (platform) {
    case "Android":
      return "text-[#3DDC84]";
    case "Windows":
      return "text-[#0078D4]";
    case "Linux":
      return "text-[#FCC624]";
    default:
      return "text-[var(--text-secondary)]";
  }
}
